import React from "react";
import { Link } from "react-router-dom";
import "./FoodMenu.css";
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  ListGroup,
  ListGroupItem
} from "reactstrap";

function FoodMenu({ snacks, drinks }) {
  let items;
  let link;
  if (snacks) {
    items = snacks;
    link = 'Snacks';
  }
  else if (drinks) {
    item= drinks;
    link = 'Drinks';
  }
  return (
    <section className="col-md-4">
      <Card>
        <CardBody>
          <CardTitle className="font-weight-bold text-center">
            Food Menu
          </CardTitle>
          <CardText>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </CardText>
          <ListGroup>
            {snacks.map(snack => (
              <Link to={`/snacks/${snack.id}`} key={snack.id}>
                <ListGroupItem>{snack.name}</ListGroupItem>
              </Link>
            ))}
          </ListGroup>
            {drinks.map(drinks => (
              <Link to={`/drinks/${drinks.id}`} key={drinks.id}>
                <ListGroupItem>{drinks.name}</ListGroupItem>
              </Link>
            ))}
        </CardBody>
      </Card>
    </section>
  );
}

export default FoodMenu;
