import React from "react";
import ItemForm from "./ItemForm";
import { useState } from "react";
import { Link } from "react-router-dom";

function Add({setSnacks,setDrinks,currentSnacks,currentDrinks}) {
    const [data, setData] = useState("")

    if (data) {
        const previousDrinks = currentDrinks
        setDrinks (currentDrinks.push(data.name))
        return (
            <div>
            <p>{data.name} added!</p>
            <p>Note: Please allow infinite time to have the {data.item} added as we currently have no actual database.</p>
            <p>previousDrinks. {previousDrinks}</p>
            <p>newDrinks. {currentDrinks}</p>
            <p>
            <b>Recipe:</b> {}
          </p>
          <p>
            <b>Serve:</b> {}
          </p>
            <Link to="/">Go Home</Link>
            </div>
            )
    }
    return <ItemForm setData={setData}/>

}

export default Add;
