import React from "react";
import ItemForm from "./ItemForm";
import { useState } from "react";
import { Link } from "react-router-dom";

function Add({ setSnacks, setDrinks, currentSnacks, currentDrinks }) {
  const [data, setData] = useState({
    name: "",
    description: "",
    recipe: "",
    serve: "",
  });

  if (data.name) {
    // Create a new item object
    const newItem = {
      name: data.name,
      description: data.description,
      recipe: data.recipe,
      serve: data.serve,
    };

    // Update the appropriate list (snacks or drinks)
    if (data.type === "drink") {
      setDrinks([...currentDrinks, newItem]);
    } else {
      setSnacks([...currentSnacks, newItem]);
    }

    return (
      <div>
        <p>{data.name} added!</p>
        <p>
          Note: Please allow infinite time to have the {data.name} added as we
          currently have no actual database.
        </p>
        <p>
          <b>Description:</b> {data.description}
        </p>
        <p>
          <b>Recipe:</b> {data.recipe}
        </p>
        <p>
          <b>Serve:</b> {data.serve}
        </p>
        <Link to="/">Go Home</Link>
      </div>
    );
  }

  return <ItemForm setData={setData} />;
}

export default Add;
